from typing import List


class Solution:
    def threeSum(self, nums: List[int]) -> List[List[int]]:
        res = set()

        d = {}
        for i in range(len(nums)):
            for j in range(i):
                s = nums[i] + nums[j]
                idx = d.get(-s, j)
                if idx < j:
                    res.add(tuple(sorted((nums[idx], nums[j], nums[i]))))  # increasing order
            d[nums[i]] = d.get(nums[i], i)

        return list(list(item) for item in res)


